CREATE OR REPLACE FUNCTION get_if_rule_preceptor (cd_pessoa_estudante_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exist_w varchar(1);


BEGIN

select 	coalesce(max('S'),'N')
into STRICT	ie_exist_w
from 	pessoa_fisica_estudante
where 	cd_pessoa_fisica = cd_pessoa_estudante_p
and     coalesce(IE_SITUACAO,'A') = 'A'
and		clock_timestamp() between coalesce(dt_inicio,clock_timestamp()) and coalesce(dt_fim,clock_timestamp());

return ie_exist_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
