CREATE OR REPLACE FUNCTION get_if_use_episode ( cd_pessoa_fisica_p episodio_paciente.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


ds_return_w 	varchar(1);
is_use_case_w    varchar(1);


BEGIN

ds_return_w := 'S';

select  obter_uso_case(wheb_usuario_pck.get_nm_usuario)
into STRICT    is_use_case_w
;

if ((cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and is_use_case_w = 'S') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_return_w
	from	episodio_paciente a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
    and coalesce(dt_cancelamento::text, '') = '';
end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
