SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_ihc_transaction (nr_account_p ihc_claim.cd_transaction%type) RETURNS varchar AS $body$
DECLARE


cd_transaction_w 		ihc_claim.cd_transaction%type;
ihc_transaction_w		ihc_claim.cd_transaction%type;
sts_resp_transaction_w	sts_response.cd_transaction%type;
sts_req_transaction_w	sts_request.cd_transaction%type;


BEGIN

	select *
	into STRICT ihc_transaction_w
	from (	SELECT max(cd_transaction)
		    from ihc_claim
		    where nr_account = nr_account_p
		    order by dt_atualizacao desc) alias1 LIMIT 1;

	select *
	into STRICT 	sts_resp_transaction_w
	from (	SELECT 	max(a.cd_transaction)
			from sts_response a,
			     sts_request b
			where a.nr_seq_request = b.nr_sequencia
			and b.nr_interno_conta = nr_account_p
			order by a.dt_atualizacao desc) alias1 LIMIT 1;

	select *
	into STRICT 	sts_req_transaction_w
	from (SELECT 	max(cd_transaction)
		from sts_request b
		where nr_interno_conta = nr_account_p
		order by dt_atualizacao desc) alias1 LIMIT 1;

	if (ihc_transaction_w IS NOT NULL AND ihc_transaction_w::text <> '') then
		cd_transaction_w := ihc_transaction_w;
	elsif (sts_req_transaction_w IS NOT NULL AND sts_req_transaction_w::text <> '') then
		cd_transaction_w := sts_req_transaction_w;
	elsif (sts_resp_transaction_w IS NOT NULL AND sts_resp_transaction_w::text <> '') then
		cd_transaction_w := sts_resp_transaction_w;
	end if;

	return cd_transaction_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
