CREATE OR REPLACE FUNCTION get_image_medicine ( cd_material_p bigint) RETURNS text AS $body$
DECLARE


image_path_w bytea;

BEGIN
  if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	  select IM_MATERIAL into STRICT image_path_w from MATERIAL_IMAGEM where CD_MATERIAL = cd_material_p  LIMIT 1;
	end if;
	return image_path_w;
	exception
	when others then
	return null;
end;
$body$
