SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_information_surgery_report ( nr_seq_episodio_p atendimento_paciente.nr_seq_episodio%type ) RETURNS varchar AS $body$
DECLARE


nr_cirurgia_w			cirurgia.nr_cirurgia%type;
nr_medical_letter_w		carta_medica.nr_sequencia%type;
ie_return_w				varchar(2);
ie_concluida_w			varchar(2);


BEGIN
	select	max(nr_cirurgia)
	into STRICT	nr_cirurgia_w
	from	cirurgia
	where	nr_atendimento in ( SELECT  nr_atendimento
                                from    atendimento_paciente
                                where   NR_SEQ_EPISODIO = nr_seq_episodio_p );

	select  max('C')
	into STRICT	ie_concluida_w
	from    cirurgia
	where   nr_cirurgia = nr_cirurgia_w
	and     IE_STATUS_CIRURGIA not in (3,4)
	and     (dt_inicio_prevista IS NOT NULL AND dt_inicio_prevista::text <> '')
	and     (dt_inicio_real IS NOT NULL AND dt_inicio_real::text <> '')
	and     (dt_termino IS NOT NULL AND dt_termino::text <> '');
	
	select	max(nr_sequencia)
	into STRICT	nr_medical_letter_w
	from	carta_medica
	where	nr_cirurgia = nr_cirurgia_w;
	
	if (coalesce(nr_cirurgia_w::text, '') = '') then
		ie_return_w := 'GY';
	elsif (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '' AND ie_concluida_w IS NOT NULL AND ie_concluida_w::text <> '') then
		ie_return_w := 'G';
	elsif ((nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') and coalesce(nr_medical_letter_w::text, '') = '') then
		ie_return_w := 'R';
	elsif ((nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') and coalesce(ie_concluida_w::text, '') = '') then
		ie_return_w := 'Y';
	end if;

	return ie_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
