CREATE OR REPLACE FUNCTION get_infusion_rate_diff ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_etapa_p bigint) RETURNS bigint AS $body$
DECLARE

qt_dosagem_w prescr_solucao_esquema.qt_dosagem%type;

BEGIN
	begin
		select	qt_dosagem
		into STRICT 	qt_dosagem_w
		from	prescr_solucao_esquema
		where 	nr_prescricao = nr_prescricao_p
		and 	nr_seq_solucao 	= nr_sequencia_p
		and 	nr_etapa 	= nr_seq_etapa_p;
		
	exception
	when others then
	    null;
	end;
return  qt_dosagem_w;
end;
