SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_insurance_details_aus ( patientid_p bigint, insurance_p text, type_information_p text) RETURNS varchar AS $body$
DECLARE



ds_return_w        	varchar(15);
member_id_w		pessoa_titular_convenio.cd_usuario_convenio%type;
nr_position_card_w	pessoa_titular_convenio.cd_usuario_convenio%type;
category_id_w		pessoa_titular_convenio.cd_categoria%type;
insurance_id_w		convenio.cd_integracao%type;
fund_group_id_w		convenio.cd_externo%type;
cd_integracao_w		categoria_convenio.CD_INTEGRACAO%type;
cd_convenio_w		categoria_convenio.cd_convenio%type;



BEGIN

begin

if (insurance_p = 'MED') then

	select	substr(a.cd_usuario_convenio,1,10) member_id,
		substr(a.cd_usuario_convenio,11,1) nr_position_card
	into STRICT	member_id_w,
		nr_position_card_w
	from	pessoa_titular_convenio a,
		convenio b
	where	a.cd_pessoa_fisica 	= patientid_p
	and	b.cd_convenio 		    = a.cd_convenio
	and 	b.ie_tipo_convenio 	= 12;


	if (member_id_w IS NOT NULL AND member_id_w::text <> '') and (coalesce(nr_position_card_w::text, '') = '') then
		nr_position_card_w	:= 1;
	end if;

elsif (insurance_p = 'DVA') then

	select	a.cd_usuario_convenio member_id,
		a.cd_categoria category_id,
		a.cd_convenio
	into STRICT	member_id_w,
		category_id_w,
		cd_convenio_w
	from	pessoa_titular_convenio a,
		convenio b
	where	a.cd_pessoa_fisica 	= patientid_p
	and	    b.cd_convenio 		= a.cd_convenio
	and 	b.ie_tipo_convenio  = 13;
	
	select	max(cd_integracao)
	into STRICT	cd_integracao_w
	from	categoria_convenio
	where	cd_convenio	= cd_convenio_w
	and	cd_categoria	= category_id_w;
	
	

elsif (insurance_p = 'HFUND') then

	select	max(a.cd_usuario_convenio) member_id,
		max(b.CD_INTEGRACAO) insurance_id,
		max(b.cd_externo) fund_group_id
	into STRICT	member_id_w,
		insurance_id_w,
		fund_group_id_w
	from	pessoa_titular_convenio a,
		convenio b
	where	a.cd_pessoa_fisica	= patientid_p
	and	b.cd_convenio		= a.cd_convenio
	and 	b.ie_tipo_convenio not in (12, 13);

end if;

if (type_information_p = 'MN') then
	ds_return_w := member_id_w;
elsif (type_information_p = 'POC') then
	ds_return_w := nr_position_card_w;
elsif (type_information_p = 'DN' ) then
	ds_return_w := member_id_w;
elsif (type_information_p = 'DT') then
	ds_return_w := cd_integracao_w;
elsif (type_information_p = 'HN') then
	ds_return_w := member_id_w;
elsif (type_information_p = 'HFI') then
	ds_return_w := insurance_id_w;
elsif (type_information_p = 'HFM') then
	ds_return_w := fund_group_id_w;
end if;

exception
when others then
	ds_return_w := null;
end;

return    ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION get_insurance_details_aus ( patientid_p bigint, insurance_p text, type_information_p text) FROM PUBLIC;

