CREATE OR REPLACE FUNCTION get_insurance_holder_sequencia (cd_pessoa_fisica_p bigint, ie_insurance_type_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w bigint := null;
cd_convenio_w  bigint := null;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND ie_insurance_type_p IS NOT NULL AND ie_insurance_type_p::text <> '') then
	begin
		select	max(cd_convenio)
		into STRICT	cd_convenio_w
		from	convenio
		where	ie_tipo_convenio 	= ie_insurance_type_p;
	
		select	nr_sequencia
		into STRICT	nr_sequencia_w
		from (SELECT	nr_sequencia
			from	pessoa_titular_convenio a
			where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
			and	a.cd_convenio        = cd_convenio_w
			and (a.dt_inicio_vigencia <= clock_timestamp() or coalesce(a.dt_inicio_vigencia::text, '') = '')
			order by dt_fim_vigencia desc) alias3 LIMIT 1;
	exception
		when	no_data_found then
			nr_sequencia_w := null;
	end;
	return nr_sequencia_w;
end if;
return nr_sequencia_w;
end;
