CREATE OR REPLACE FUNCTION get_insurance_holder_seq_med (cd_pesso_fisica_p bigint, cd_usuario_convenio text) RETURNS bigint AS $body$
DECLARE

ie_reterno_p varchar(2) := 'N';
nr_sequencia_w bigint := null;
cd_convenio_w  bigint := null;


BEGIN
	select cd_convenio
	into STRICT cd_convenio_w
	from convenio
	where upper(cd_externo) = 'MED';

	if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') then
	begin
		select nr_sequencia
		into STRICT nr_sequencia_w
		from (SELECT nr_sequencia
			from pessoa_titular_convenio a
			where a.cd_pessoa_fisica = cd_pesso_fisica_p
			and a.cd_convenio        = cd_convenio_w
			and a.dt_inicio_vigencia < clock_timestamp()
			order by dt_fim_vigencia desc
			) alias2 LIMIT 1;
	end;
		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			ie_reterno_p := 'S';
		end if;
	end if;
end;
