CREATE OR REPLACE FUNCTION get_ins_rec_strt_exp_date ( cd_pessoa_fisica_p bigint, ie_insurance_type_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

ie_retorno_w   timestamp;
nr_sequencia_w bigint;

BEGIN
nr_sequencia_w := get_insurance_holder_sequencia(cd_pessoa_fisica_p,ie_insurance_type_p);
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
/*
ie_opcao_p
effective as of  'S'
expiration date  'E'
*/
		case ie_opcao_p
			when 'S' then
				select 	dt_inicio_vigencia
				into STRICT 	ie_retorno_w
				from 	pessoa_titular_convenio
				where 	nr_sequencia = nr_sequencia_w;
			when 'E' then
				select 	dt_fim_vigencia
				into STRICT 	ie_retorno_w
				from 	pessoa_titular_convenio
				where 	nr_sequencia = nr_sequencia_w;
		end case;
	end if;
return ie_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
