CREATE OR REPLACE FUNCTION get_item_type_cpoe_ack ( ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


/* NOTE
IE_TIPO_ITEM_P is based in the item type from eMAR
IE_TIPO_ITEM_CPOE_W is based in the item type from cpoe_acknowledge_v  
*/
ie_tipo_item_cpoe_w	varchar(1) := null;


BEGIN
	if (ie_tipo_item_p in ('M', 'MAT', 'S', 'SOL')) then
		ie_tipo_item_cpoe_w := 'M';
	elsif (ie_tipo_item_p in ('D', 'SNE', 'LD', 'NAN', 'NPN', 'NPP', 'J')) then
		ie_tipo_item_cpoe_w := 'N';
	elsif (ie_tipo_item_p = 'R') then
		ie_tipo_item_cpoe_w := 'R';
	elsif (ie_tipo_item_p = 'O') then
		ie_tipo_item_cpoe_w := 'G';
	elsif (ie_tipo_item_p in ('P', 'G', 'C', 'I', 'L')) then
		ie_tipo_item_cpoe_w := 'P';
	elsif (ie_tipo_item_p = 'DI') then
		ie_tipo_item_cpoe_w := 'D';
	elsif (ie_tipo_item_p = 'HM') then
		ie_tipo_item_cpoe_w := 'H';
	elsif (ie_tipo_item_p = 'AP') then
		ie_tipo_item_cpoe_w := 'AP';
	end if;

return ie_tipo_item_cpoe_w;

end;
