CREATE OR REPLACE FUNCTION get_japan_diagnosis (nr_atendimento_p bigint, ie_classificacao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_disease_number_w			diagnostico_doenca.nr_seq_disease_number%type;
ie_side_modifier_w          diagnostico_doenca.ie_side_modifier%type;
nr_seq_jap_pref_1_w         diagnostico_doenca.nr_seq_jap_pref_1%type;
nr_seq_jap_pref_2_w         diagnostico_doenca.nr_seq_jap_pref_2%type;
nr_seq_jap_pref_3_w         diagnostico_doenca.nr_seq_jap_pref_3%type;
nr_seq_jap_sufi_1_w         diagnostico_doenca.nr_seq_jap_sufi_1%type;
nr_seq_jap_sufi_2_w         diagnostico_doenca.nr_seq_jap_sufi_2%type;
nr_seq_jap_sufi_3_w         diagnostico_doenca.nr_seq_jap_sufi_3%type;
dt_diagnostico_w		        timestamp;
ds_return_w                 varchar(255);

C01 CURSOR FOR
	SELECT	a.dt_diagnostico
	from	diagnostico_medico a
	where	a.nr_atendimento	= nr_atendimento_p
	and	exists (SELECT	1
		from	diagnostico_doenca x
		where	x.nr_atendimento	= a.nr_atendimento
		and		x.dt_diagnostico	= a.dt_diagnostico
		and		coalesce(x.ie_situacao,'A') = 'A')
	order by	ie_tipo_diagnostico desc, dt_diagnostico desc;


BEGIN
nr_seq_disease_number_w	:= 0;

begin
	OPEN C01;
	LOOP
	FETCH C01 into
		dt_diagnostico_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		BEGIN
		begin
		select nr_seq_disease_number,
              ie_side_modifier,
              nr_seq_jap_pref_1,
              nr_seq_jap_pref_2,
              nr_seq_jap_pref_3,
              nr_seq_jap_sufi_1,
              nr_seq_jap_sufi_2,
              nr_seq_jap_sufi_3
	   into STRICT  nr_seq_disease_number_w,
            ie_side_modifier_w,
            nr_seq_jap_pref_1_w,
            nr_seq_jap_pref_2_w,
            nr_seq_jap_pref_3_w,
            nr_seq_jap_sufi_1_w,
            nr_seq_jap_sufi_2_w,
            nr_seq_jap_sufi_3_w
		from diagnostico_doenca
		where nr_atendimento = nr_atendimento_p
		and dt_diagnostico = dt_diagnostico_w
		and	ie_classificacao_doenca	= ie_classificacao_p;
		exception
				when others then
				nr_seq_disease_number_w	:= 0;
		end;
		if (coalesce(nr_seq_disease_number_w , 0) > 0) then
        ds_return_w := get_desc_modify_disease_epc(nr_seq_disease_number_w,
            ie_side_modifier_w,
            nr_seq_jap_pref_1_w,
            nr_seq_jap_pref_2_w,
            nr_seq_jap_pref_3_w,
            nr_seq_jap_sufi_1_w,
            nr_seq_jap_sufi_2_w,
            nr_seq_jap_sufi_3_w);
		end if;
		END;
	END LOOP;
	CLOSE C01;
end;
RETURN ds_return_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
