SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_jp_insurance_default (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE



nr_seq_insurance_w              bigint;
ie_exists_insurance_pattern_w   smallint;


BEGIN


select  coalesce(max(1),0)
into STRICT    ie_exists_insurance_pattern_w
from    nais_insurance a
where   not exists (SELECT 1 from convenio x where x.cd_externo = to_char(a.cd_insurance_pattern))
and     a.cd_pessoa_fisica = cd_pessoa_fisica_p;


-- When patient has no insurance pattern from NAIS, only Self Pay
if (ie_exists_insurance_pattern_w = 0) then

  select  max(nr_sequencia)
  into STRICT    nr_seq_insurance_w
  from    nais_insurance a
  where   exists (SELECT 1 from convenio x where x.cd_externo = to_char(a.cd_insurance_pattern))
  and     a.cd_pessoa_fisica = cd_pessoa_fisica_p;

else
-- When patient has insurance pattern from NAIS and Self Pay
  select 	max(a.nr_sequencia)
  into STRICT    nr_seq_insurance_w        	
  from 	  nais_insurance a
  where   a.cd_pessoa_fisica = cd_pessoa_fisica_p
  and     a.ie_default = 'S';

end if;

return nr_seq_insurance_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
