SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_jp_md_default (nm_usuario_p text, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_medical_department_w   bigint;


BEGIN

if (obter_se_usuario_medico(nm_usuario_p) = 'S') then

  select  max(a.cd_departamento)
  into STRICT    cd_medical_department_w
  from    departamento_medico a,
		  departamento_espec b
  where   a.ie_situacao = 'A'
  and     clock_timestamp() between coalesce(a.dt_inicial,clock_timestamp() - interval '1 days') and coalesce(a.dt_final,clock_timestamp() + interval '1 days')
  and     a.cd_departamento = b.cd_departamento
  and     b.cd_especialidade in (SELECT y.cd_especialidade
				  from (select x.cd_especialidade
					from  medico_especialidade x
					where x.cd_pessoa_fisica = obter_pf_usuario(nm_usuario_p,'C')
					order by x.nr_seq_prioridade desc) y LIMIT 1);

else
  cd_medical_department_w := obter_departamento_atual_atend(nr_atendimento_p);
end if;

return cd_medical_department_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
