CREATE OR REPLACE FUNCTION get_justification_under_aged ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, cd_convenio_p convenio.cd_convenio%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS bigint AS $body$
DECLARE

	
is_younger_age_w	    	varchar(1);
qt_insurance_without_fee_w  	smallint;
nr_seq_justificativa_w		pessoa_fisica_taxa.nr_seq_justificativa%type;


BEGIN

nr_seq_justificativa_w := 0;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then

	select	coalesce(obter_se_menor_idade(cd_pessoa_fisica_p), 'N')
	into STRICT	is_younger_age_w
	;
		
	if (is_younger_age_w = 'S') then

		select 	count(1)
		into STRICT 	qt_insurance_without_fee_w
		from 	convenio con
		where 	con.cd_convenio 	= cd_convenio_p
		and   	obter_regra_taxa_convenio(con.ie_tipo_convenio, con.cd_convenio) = 'S'
		and (coalesce(nr_atendimento_p::text, '') = '' or (	SELECT 	count(1)
				from	atendimento_paciente ap
				where  	ap.nr_atendimento = nr_atendimento_p
				and 	ap.ie_tipo_atendimento = 1) > 0);
			
		if (qt_insurance_without_fee_w > 0) then
            nr_seq_justificativa_w := obter_param_usuario(916, 1218, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, obter_estabelecimento_ativo, nr_seq_justificativa_w);
		end if;
				
	end if;
end if;

return nr_seq_justificativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
