CREATE OR REPLACE FUNCTION get_kv_status ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_w	varchar(7);


BEGIN

select	CASE WHEN c.ie_tipo_conveniado=1 THEN '1' WHEN c.ie_tipo_conveniado=2 THEN '3' WHEN c.ie_tipo_conveniado=5 THEN '5'  ELSE '' END ||
	lpad(coalesce(a.besondere_personengruppe,0),2,0)||
	lpad(coalesce(a.dmp_kennzeichnung,0),2,0)||
	CASE WHEN coalesce(obter_asv_team(b.nr_atendimento)::text, '') = '' THEN  CASE WHEN d.ie_tipo_consentimento='E' THEN '04'  ELSE '00' END   ELSE lpad('01',2,0) END  ds_status
into STRICT	ds_status_w
FROM atend_categoria_convenio c, atendimento_paciente b
LEFT OUTER JOIN pessoa_fisica_egk a ON (b.cd_pessoa_fisica = a.cd_pessoa_fisica)
LEFT OUTER JOIN pep_pac_ci d ON (b.nr_atendimento = d.nr_atendimento)
WHERE b.nr_atendimento	= c.nr_atendimento AND c.nr_seq_interno	= ( SELECT MAX(t.nr_seq_interno) FROM atend_categoria_convenio t, convenio x WHERE t.nr_atendimento = c.nr_atendimento AND t.cd_convenio = x.cd_convenio AND x.ie_tipo_convenio = 11 )  and b.nr_atendimento	= nr_atendimento_p;

return	ds_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
