CREATE OR REPLACE FUNCTION get_license_number ( cd_doctor_p text, si_special_license_p text, dt_reference_p timestamp default null) RETURNS varchar AS $body$
DECLARE

			
nr_document_w	pessoa_documentacao.nr_document%type;

c01 CURSOR FOR
	SELECT	nr_document
	from	pessoa_documentacao
	where	cd_pessoa_fisica = cd_doctor_p
	and	si_special_license = si_special_license_p
	and	coalesce(dt_validade,clock_timestamp()) >= coalesce(dt_reference_p,clock_timestamp())
	and	ie_situacao = 'A'
	order	by dt_validade;

BEGIN
open c01;
loop

fetch c01 into
	nr_document_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

end loop;
close c01;

return	nr_document_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
