CREATE OR REPLACE FUNCTION get_loan_instlmnt_pymnt_amt ( NR_TITULO_P bigint ) RETURNS bigint AS $body$
DECLARE


VL_PAGO_W bigint := null;


BEGIN

if (NR_TITULO_P IS NOT NULL AND NR_TITULO_P::text <> '') then

  select sum(x.vl_pago)
  into STRICT VL_PAGO_W
  from titulo_pagar_baixa x
  where x.nr_titulo = NR_TITULO_P;

end	if;

  RETURN VL_PAGO_W;

END;
