CREATE OR REPLACE FUNCTION get_loan_instlmnt_pymnt_date ( NR_TITULO_P bigint ) RETURNS timestamp AS $body$
DECLARE


DT_PAGAMENTO_W timestamp := null;


BEGIN

if (NR_TITULO_P IS NOT NULL AND NR_TITULO_P::text <> '') then

  select x.dt_liquidacao
  into STRICT DT_PAGAMENTO_W
  from titulo_pagar x
  where x.nr_titulo = NR_TITULO_P
  and x.IE_SITUACAO <> 'C';

end	if;

  RETURN DT_PAGAMENTO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
