CREATE OR REPLACE FUNCTION get_md5_hash_value (message_p text) RETURNS varchar AS $body$
DECLARE


  hash_value  varchar(255) := '';

  c01 REFCURSOR;

BEGIN
  if (dbms_db_version.version < '12') then
    hash_value := dbms_crypto.hash(encode(message_p::bytea, 'hex')::bytea, dbms_crypto.hash_md5);
  else
    open c01 for EXECUTE 'SELECT STANDARD_HASH(:message_p, ''MD5'') FROM dual' using message_p;
    fetch c01
    into hash_value;
    close c01;
  end if;

  RETURN hash_value;
END;
$body$
LANGUAGE PLPGSQL
