CREATE OR REPLACE FUNCTION get_medical_record (nr_seq_estrutura_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

  ds_return_w             varchar(1) := 'N';
  nr_found_w              bigint;
  nr_count_w              bigint;
  cd_medical_department_w varchar(25);
  c01 CURSOR FOR
    SELECT cd_departamento 
    FROM   pi_estrutura_medical_dep 
    WHERE  nr_seq_estrutura = nr_seq_estrutura_p;

BEGIN
    SELECT Count(*) 
    INTO STRICT   nr_count_w 
    FROM   pi_estrutura_medical_dep 
    WHERE  nr_seq_estrutura = nr_seq_estrutura_p;

    IF ( nr_count_w = 0 ) THEN 
      ds_return_w := 'T';
    ELSE 
      OPEN c01;

      LOOP 
          FETCH c01 INTO cd_medical_department_w;

          EXIT WHEN NOT FOUND; /* apply on c01 */

          SELECT Count(*) 
          INTO STRICT   nr_found_w 
           
          WHERE  cd_medical_department_w IN (SELECT cd_departamento 
                                             FROM   user_medical_department 
                                             WHERE  nm_usuario = nm_usuario_p);

          IF ( nr_found_w > 0 ) THEN 
            ds_return_w := 'S';

            EXIT;
          END IF;
      END LOOP;

      CLOSE c01;
    END IF;

    RETURN ds_return_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
