CREATE OR REPLACE FUNCTION get_medicine_concentration ( cd_material_p text) RETURNS bigint AS $body$
DECLARE


total_conentration_w		double precision := 0.0000;
cd_unid_med_concetracao_w varchar(10);
qt_concentracao_total_w double precision;
cd_unid_med_conc_total_w varchar(10);
qt_conversao_mg_w double precision;
qt_dose_diaria_w double precision;
cd_unidade_medida_w varchar(10);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
SELECT m.cd_unid_med_concetracao,
m.qt_concentracao_total ,
m.cd_unid_med_conc_total,
m.qt_conversao_mg,
c.qt_dose_diaria,
c.cd_unidade_medida into STRICT cd_unid_med_concetracao_w,qt_concentracao_total_w,cd_unid_med_conc_total_w,qt_conversao_mg_w,qt_dose_diaria_w,cd_unidade_medida_w
FROM
material m inner join
cih_medicamento c on c.cd_medicamento = m.cd_medicamento and m.cd_material = cd_material_p;

if ((UPPER(cd_unid_med_conc_total_w) = UPPER(cd_unidade_medida_w)) and (qt_concentracao_total_w IS NOT NULL AND qt_concentracao_total_w::text <> '')) then

    select CASE WHEN UPPER(cd_unid_med_conc_total_w)='MG' THEN qt_concentracao_total_w/1000 WHEN UPPER(cd_unid_med_conc_total_w)='MCG' THEN qt_concentracao_total_w/1000000  ELSE qt_concentracao_total_w END  into STRICT total_conentration_w;

elsif ((UPPER(cd_unid_med_conc_total_w) != UPPER(cd_unidade_medida_w)) and (qt_concentracao_total_w IS NOT NULL AND qt_concentracao_total_w::text <> '')) then

    select CASE WHEN UPPER(cd_unid_med_conc_total_w)='MG' THEN qt_concentracao_total_w/1000 WHEN UPPER(cd_unid_med_conc_total_w)='MCG' THEN qt_concentracao_total_w/1000000  ELSE qt_concentracao_total_w END  into STRICT total_conentration_w;

elsif ((UPPER(cd_unid_med_concetracao_w) = UPPER(cd_unidade_medida_w )) and (qt_conversao_mg_w IS NOT NULL AND qt_conversao_mg_w::text <> ''))then

    select  CASE WHEN UPPER(cd_unid_med_concetracao_w)='MG' THEN qt_conversao_mg_w/1000 WHEN UPPER(cd_unid_med_concetracao_w)='MCG' THEN qt_conversao_mg_w/1000000  ELSE qt_conversao_mg_w END  into STRICT total_conentration_w;

elsif ((UPPER(cd_unid_med_concetracao_w) != UPPER(cd_unidade_medida_w )) and (qt_conversao_mg_w IS NOT NULL AND qt_conversao_mg_w::text <> ''))then

    select  CASE WHEN UPPER(cd_unid_med_concetracao_w)='MG' THEN qt_conversao_mg_w/1000 WHEN UPPER(cd_unid_med_concetracao_w)='MCG' THEN qt_conversao_mg_w/1000000  ELSE qt_conversao_mg_w END  into STRICT total_conentration_w;

elsif (coalesce(cd_unidade_medida_w::text, '') = '' and (cd_unid_med_conc_total_w IS NOT NULL AND cd_unid_med_conc_total_w::text <> '') and (qt_concentracao_total_w IS NOT NULL AND qt_concentracao_total_w::text <> '')) then

    select CASE WHEN UPPER(cd_unid_med_conc_total_w)='MG' THEN qt_concentracao_total_w/1000 WHEN UPPER(cd_unid_med_conc_total_w)='MCG' THEN qt_concentracao_total_w/1000000  ELSE qt_concentracao_total_w END  into STRICT total_conentration_w;

elsif (coalesce(cd_unidade_medida_w::text, '') = '' and coalesce(qt_concentracao_total_w::text, '') = '' and (qt_conversao_mg_w IS NOT NULL AND qt_conversao_mg_w::text <> ''))then

    select CASE WHEN UPPER(cd_unid_med_concetracao_w)='MG' THEN qt_conversao_mg_w/1000 WHEN UPPER(cd_unid_med_concetracao_w)='MCG' THEN qt_conversao_mg_w/1000000  ELSE qt_conversao_mg_w END  into STRICT total_conentration_w;

end if;

END IF;

RETURN total_conentration_w;

END;
$body$
