CREATE OR REPLACE FUNCTION get_medic_speciality_degress ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_titulacao_w		medico_titulacao.nr_seq_titulacao%type;
ds_retorno_w			pf_titulacao_especialidade.ds_titulo%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(nr_seq_titulacao)
	into STRICT	nr_seq_titulacao_w
	from	medico_titulacao
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p
	and 	(nr_seq_titulacao IS NOT NULL AND nr_seq_titulacao::text <> '')
	and		(dt_titulacao IS NOT NULL AND dt_titulacao::text <> '');
	
	if (nr_seq_titulacao_w IS NOT NULL AND nr_seq_titulacao_w::text <> '') then
	
		select 	max(ds_titulo)
		into STRICT	ds_retorno_w
		from 	pf_titulacao_especialidade
		where 	ie_situacao = 'A'
		and		nr_sequencia = nr_seq_titulacao_w;
		
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
