CREATE OR REPLACE FUNCTION get_medispan_admin_rep ( nr_prescricao_p prescr_material.nr_prescricao%type, nr_seq_material_p prescr_material.nr_seq_material%type ) RETURNS varchar AS $body$
DECLARE

	ds_return_w				varchar(1);
	ie_se_necessario_w		prescr_material.ie_se_necessario%type;
	ie_acm_w				prescr_material.ie_acm%type;

/*
	
*/
BEGIN
	ds_return_w := 'P';

	select coalesce(max(a.ie_se_necessario), 'N'),
		   coalesce(max(a.ie_acm), 'N')
	  into STRICT ie_se_necessario_w,
		   ie_acm_w
	  from prescr_material a
	 where a.nr_prescricao = nr_prescricao_p
	   and a.nr_seq_material = nr_seq_material_p;

	if ((ie_se_necessario_w = 'S') or (ie_acm_w = 'S')) then
		ds_return_w := 'N';
	end if;

	return ds_return_w;

end;
$body$
