CREATE OR REPLACE FUNCTION get_medispan_dose_type ( qt_dose_ataque_p cpoe_material.qt_dose_ataque%type, ie_evento_unico_p intervalo_prescricao.ie_dose_unica_cpoe%type ) RETURNS varchar AS $body$
DECLARE

	ds_return_w	varchar(255);

/*
	DoseType
	Initial = Dose de ataque
	Maintenance = Dose normal
	Single = Dose/Evento unica
*/
BEGIN
	ds_return_w := '';
	
	/*if (nvl(qt_dose_ataque_p, 0) > 0) then
		-- Initial Dose processa em paralelo com a Single ou Maintenance direto no fonte.
		ds_return_w := 'Initial dose';
	*/
