CREATE OR REPLACE FUNCTION get_medispan_drug_names ( nr_prescricao_p prescr_material.nr_prescricao%type, nr_seq_id_medispan_p alerta_api.nr_seq_id_medispan%type, nr_agrupamento_p alerta_api.nr_agrupamento%type, cd_api_p alerta_api.cd_api%type ) RETURNS varchar AS $body$
DECLARE

													
	ds_nome_w	varchar(2000);

	cItemsCode CURSOR FOR
		SELECT distinct obter_desc_material(a.cd_material) ds_material
		  from alerta_api b,
			   prescr_material a
		 where a.nr_prescricao = b.nr_prescricao
		   and a.nr_sequencia = b.nr_seq_material
		   and b.nr_prescricao = nr_prescricao_p
		   and b.nr_seq_id_medispan = nr_seq_id_medispan_p
		   and b.cd_api = cd_api_p
		   and b.nr_agrupamento = nr_agrupamento_p
		   and coalesce(b.ie_status_requisicao::text, '') = '';

	cItemsDesc CURSOR FOR
		SELECT distinct obter_desc_material(a.cd_material) ds_material
		  from alerta_api b,
			   prescr_material a
		 where a.nr_prescricao = b.nr_prescricao
		   and a.nr_sequencia = b.nr_seq_material
		   and b.nr_prescricao = nr_prescricao_p
		   and b.nr_agrupamento = nr_agrupamento_p
		   and b.cd_api = cd_api_p
		   and coalesce(b.ie_status_requisicao::text, '') = '';
BEGIN
	ds_nome_w := '';

	if (coalesce(nr_seq_id_medispan_p, 0) > 0) then
		for cItems_w in cItemsCode
		loop
			if (coalesce(ds_nome_w, 'XPTO') = 'XPTO') then
				ds_nome_w := cItems_w.ds_material;
			else
				ds_nome_w := ds_nome_w || ' ' || obter_desc_expressao(312342) || ' ' || cItems_w.ds_material;
			end if;
		end loop;	
	else
		for cItems_w in cItemsDesc
		loop
			if (coalesce(ds_nome_w, 'XPTO') = 'XPTO') then
				ds_nome_w := cItems_w.ds_material;
			else
				ds_nome_w := ds_nome_w || ' ' || obter_desc_expressao(312342) || ' ' || cItems_w.ds_material;
			end if;
		end loop;
	end if;

	return ds_nome_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
