CREATE OR REPLACE FUNCTION get_medispan_finish_control ( nr_prescricao_p prescr_medica.nr_prescricao%type ) RETURNS varchar AS $body$
DECLARE


	ds_return_w	varchar(1);

  r_alert_w RECORD;

BEGIN
	ds_return_w := 'S';

    <<validate_loop>>
    for r_alert_w in (SELECT a1.nr_sequencia
						from alerta_api a1
					   where a1.nr_sequencia in (select a2.nr_sequencia
												   from alerta_api a2
												  where a2.nr_prescricao = nr_prescricao_p
												    and (a2.ie_status_requisicao IS NOT NULL AND a2.ie_status_requisicao::text <> '')
												    and coalesce(a2.ie_status_requisicao, 'X') not in ('E', 'F')))
    loop
		if (r_alert_w.nr_sequencia > 0) then
			ds_return_w := 'N';
			exit;
		end if;
	end loop validate_loop;

	return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
