CREATE OR REPLACE FUNCTION get_medispan_route_rep ( ie_agrupador_p prescr_material.ie_agrupador%type, ie_via_aplicacao_p via_aplicacao.ie_via_aplicacao%type ) RETURNS varchar AS $body$
DECLARE

	
	ds_retorno_w	conversao_meio_externo.cd_externo%type;
	ie_endovenosa_w	via_aplicacao.ie_endovenosa%type;


BEGIN

	if (ie_agrupador_p = 4) then
	
		select max(a.ie_endovenosa)
		  into STRICT ie_endovenosa_w
		  from via_aplicacao a
		 where a.ie_via_aplicacao = ie_via_aplicacao_p;
		
		if (ie_endovenosa_w = 'S') then
			ds_retorno_w := 'Intravenous (Continuous Infusion)';
		else
			ds_retorno_w := obter_conv_meio_ext_int('VIA_APLICACAO','IE_VIA_APLICACAO', ie_via_aplicacao_p,'LXC');
		end if;
	else
		ds_retorno_w := obter_conv_meio_ext_int('VIA_APLICACAO','IE_VIA_APLICACAO', ie_via_aplicacao_p,'LXC');
	end if;

	return ds_retorno_w;

end;
$body$
