CREATE OR REPLACE FUNCTION get_medispan_seq_exame_creatin ( nm_tabela_p pep_dest_inf.nm_tabela%type, nm_atributo_p pep_dest_inf.nm_atributo%type, nr_sequencia_info_p pep_informacao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


	cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
	cd_empresa_w			empresa.cd_empresa%type;
	ds_seq_exame_w			varchar(2000);

	/* MESMO CURSOR UTILIZADO NA REP_OBTER_CLEARENCE */

	c01 CURSOR FOR
		SELECT	c.nr_seq_exame
		  from	pep_inf_regra c,
				pep_informacao b,
				pep_dest_inf a
		 where	a.nm_tabela = nm_tabela_p
		   and	a.nm_atributo = nm_atributo_p
		   and	c.cd_empresa = cd_empresa_w
		   and	coalesce(c.cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
		   and	a.nr_seq_inf = b.nr_sequencia
		   and	b.nr_sequencia = c.nr_seq_inf
		   and	b.nr_sequencia = nr_sequencia_info_p
		   and (a.nr_sequencia = c.nr_seq_dest_inf or coalesce(c.nr_seq_dest_inf::text, '') = '')
		   and  c.ie_informacao = 'Ultima';

BEGIN
	cd_estabelecimento_w := obter_estabelecimento_ativo;
	cd_empresa_w := obter_empresa_estab(cd_estabelecimento_w);
	ds_seq_exame_w := '';

	if ((nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') and (nm_atributo_p IS NOT NULL AND nm_atributo_p::text <> '') and (nr_sequencia_info_p IS NOT NULL AND nr_sequencia_info_p::text <> '')) then
		/* PARAMETROS ASSISTENCIAIS - REGRA INFORMACAO */

		for c01_w in c01
		loop
			ds_seq_exame_w := c01_w.nr_seq_exame || ',' || ds_seq_exame_w;
		end loop;
	end if;

	return ds_seq_exame_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
