CREATE OR REPLACE FUNCTION get_medispan_uniqueevent_rep (nr_prescricao_p prescr_material.nr_prescricao%type, nr_seq_material_p prescr_material.nr_seq_material%type ) RETURNS varchar AS $body$
DECLARE

	ds_return_w				varchar(1);
	ie_se_necessario_w		prescr_material.ie_se_necessario%type;
	ie_acm_w				prescr_material.ie_acm%type;
	ie_sem_aprazamento_w	prescr_material.ie_sem_aprazamento%type;
	cd_intervalo_w			prescr_material.cd_intervalo%type;
	qt_operacao_w			intervalo_prescricao.qt_operacao%type;
	ie_operacao_w			intervalo_prescricao.ie_operacao%type;
	ie_copiar_w				intervalo_prescricao.ie_copiar%type;

/*
	
*/
BEGIN
	ds_return_w := 'N';

	select coalesce(max(a.ie_se_necessario), 'N'),
		   coalesce(max(a.ie_acm), 'N'),
		   coalesce(max(a.ie_sem_aprazamento), 'N'),
		   coalesce(max(a.cd_intervalo), 'XPTO')
	  into STRICT ie_se_necessario_w,
		   ie_acm_w,
		   ie_sem_aprazamento_w,
		   cd_intervalo_w
	  from prescr_material a
	 where a.nr_prescricao = nr_prescricao_p
	   and a.nr_sequencia = nr_seq_material_p;

	if ((ie_se_necessario_w = 'N') and (ie_acm_w = 'N') and (ie_sem_aprazamento_w = 'N')) then

		select max(a.ie_copiar),
			   max(a.qt_operacao),
			   max(a.ie_operacao)
		  into STRICT ie_copiar_w,
			   qt_operacao_w,
			   ie_operacao_w
		  from intervalo_prescricao a
		 where a.cd_intervalo = cd_intervalo_w;

		if ((ie_copiar_w = 'N') and (qt_operacao_w = 1) and (ie_operacao_w = 'X')) then
			ds_return_w := 'S';
		end if;
	end if;

	return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
