CREATE OR REPLACE FUNCTION get_message_integration ( cd_expressao_p bigint, nr_seq_fila_p bigint) RETURNS varchar AS $body$
DECLARE


ds_locale_w		intpd_eventos_sistema.ds_locale%type := 'en_US';
ds_message_w	varchar(4000) := '';


BEGIN
if (cd_expressao_p IS NOT NULL AND cd_expressao_p::text <> '') then
	begin
	if (nr_seq_fila_p IS NOT NULL AND nr_seq_fila_p::text <> '') then
		begin
		select	coalesce(max(b.ds_locale),'en_US')
		into STRICT	ds_locale_w
		from	intpd_fila_transmissao a,
				intpd_eventos_sistema b
		where	a.nr_seq_evento_sistema = b.nr_sequencia
		and		a.nr_sequencia = nr_seq_fila_p;
		end;
	end if;
	ds_message_w := expressao_pck.obter_dic_expressao_loc(cd_expressao_p, ds_locale_w);
	end;
end if;
return ds_message_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
