CREATE OR REPLACE FUNCTION get_mhr_withdraw_consent ( nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE


qt_reg_w		bigint;	
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;

	

BEGIN

--Check the consent under the encounter
select	count(1)
into STRICT	qt_reg_w
from	pep_pac_ci
where	nr_atendimento		= nr_atendimento_p
and	ie_tipo_consentimento	= 'MHR'
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(dt_inativacao::text, '') = '';

if (qt_reg_w	> 0) then
	return 'S';
end if;

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;

--Check the consent under the patient
select	count(1)
into STRICT	qt_reg_w
from	pep_pac_ci
where	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	ie_tipo_consentimento	= 'MHR'
and	coalesce(nr_atendimento::text, '') = ''
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(dt_inativacao::text, '') = '';

if (qt_reg_w	> 0) then
	return 'S';
end if;


return	'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION get_mhr_withdraw_consent ( nr_atendimento_p atendimento_paciente.nr_atendimento%type) FROM PUBLIC;

