CREATE OR REPLACE FUNCTION get_mipres_disp_qt_dispense ( nr_seq_mat_hor_p prescr_disp_mipres.nr_seq_mat_hor%type, nr_seq_proc_hor_p prescr_disp_mipres.nr_seq_proc_hor%type ) RETURNS bigint AS $body$
DECLARE


