CREATE OR REPLACE FUNCTION get_mipres_document_type ( NR_ATENDIMENTO_P ATENDIMENTO_PACIENTE.NR_ATENDIMENTO%TYPE, CD_PESSOA_FISICA_P ATENDIMENTO_PACIENTE.CD_PESSOA_FISICA%TYPE DEFAULT NULL, IE_VALOR_DOMINIO_P text DEFAULT 'S') RETURNS varchar AS $body$
DECLARE


	nr_seq_tipo_doc_w	pessoa_fisica_aux.nr_seq_tipo_doc_identificacao%type;
	ds_retorno_w	varchar(255)  := null;


BEGIN

    if (coalesce(CD_PESSOA_FISICA_P::text, '') = '') then
        select	max(b.nr_seq_tipo_doc_identificacao)
          into STRICT	nr_seq_tipo_doc_w
          from	atendimento_paciente a,
                pessoa_fisica_aux b
         where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
           and 	nr_atendimento = nr_atendimento_p;
    else
        select	max(b.nr_seq_tipo_doc_identificacao)
          into STRICT	nr_seq_tipo_doc_w
          from	pessoa_fisica_aux b
         where	b.cd_pessoa_fisica = cd_pessoa_fisica_p;
    end if;

    if (IE_VALOR_DOMINIO_P = 'S') then
		ds_retorno_w := obter_valor_dominio(10438, nr_seq_tipo_doc_w);
	else
		ds_retorno_w := nr_seq_tipo_doc_w;
	end if;
	
	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
