CREATE OR REPLACE FUNCTION get_next_table_sequence (nm_table_p text) RETURNS bigint AS $body$
DECLARE

			
sql_w		varchar(255);
nr_sequence_w	bigint;
			

BEGIN
	if (nm_table_p IS NOT NULL AND nm_table_p::text <> '') then
                begin
                        sql_w	:= 'SELECT MIN(a.NR_SEQUENCIA + 1) NEXT_SEQ FROM ' || nm_table_p || ' a '
                                || 'WHERE NOT EXISTS (SELECT 1 FROM ' || nm_table_p || ' b WHERE b.NR_SEQUENCIA = a.NR_SEQUENCIA + 1)';

                        EXECUTE sql_w INTO STRICT nr_sequence_w;
                end;
        end if;
return coalesce(nr_sequence_w, 1);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
