CREATE OR REPLACE FUNCTION get_nr_next_signature (nr_seq_main_daily_rep_p bigint) RETURNS bigint AS $body$
DECLARE

	

nr_next_signature_w			smallint := 0;
dt_signature_1_w		agreg_signatures.dt_signature_1%type;
dt_signature_2_w		agreg_signatures.dt_signature_2%type;
dt_signature_3_w		agreg_signatures.dt_signature_3%type;
dt_signature_4_w		agreg_signatures.dt_signature_4%type;
dt_signature_5_w		agreg_signatures.dt_signature_5%type;


BEGIN

	select max(dt_signature_1),
		   max(dt_signature_2),
		   max(dt_signature_3),
	       max(dt_signature_4),
	       max(dt_signature_5)
	into STRICT   dt_signature_1_w,
		   dt_signature_2_w,
		   dt_signature_3_w,
		   dt_signature_4_w,
		   dt_signature_5_w
	from   agreg_signatures
	where  nr_seq_main_daily_rep = nr_seq_main_daily_rep_p;
	
	if (coalesce(dt_signature_1_w::text, '') = '') then
		nr_next_signature_w := 1;
	elsif (coalesce(dt_signature_2_w::text, '') = '') then
		nr_next_signature_w := 2;
	elsif (coalesce(dt_signature_3_w::text, '') = '') then
		nr_next_signature_w := 3;
	elsif (coalesce(dt_signature_4_w::text, '') = '') then
		nr_next_signature_w := 4;
	elsif (coalesce(dt_signature_5_w::text, '') = '') then
		nr_next_signature_w := 5;
	else
		nr_next_signature_w := 0;
	end if;

return nr_next_signature_w;
end;
