CREATE OR REPLACE FUNCTION get_nr_seq_gestao_vaga ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_gestao_vaga_w	bigint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin

		select	a.nr_sequencia
		into STRICT	nr_seq_gestao_vaga_w
		from	gestao_vaga a
		where	a.ie_status in ('O','F','P','H')
		and		a.nr_atendimento = nr_atendimento_p
		and		a.dt_solicitacao = (	SELECT	max(x.dt_solicitacao)
										from	gestao_vaga x
										where	x.ie_status in ('O','F','P','H')
										and		x.nr_atendimento = nr_atendimento_p);
	end;
end if;

return	nr_seq_gestao_vaga_w;

end;
