SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_nr_seq_order_unit ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

  nr_seq_order_unit_w cpoe_order_unit.nr_sequencia%type;
  dt_liberacao_w cpoe_order_unit.dt_liberacao%type;

BEGIN
  nr_seq_order_unit_w := NULL;
  IF (nr_sequencia_p   > 0) THEN
    SELECT MAX(a.nr_seq_cpoe_order_unit)
    INTO STRICT nr_seq_order_unit_w
    FROM cpoe_material a
    WHERE a.nr_sequencia = nr_sequencia_p;
    SELECT MAX(dt_liberacao)
    INTO STRICT dt_liberacao_w
    FROM cpoe_order_unit
    WHERE nr_sequencia     = nr_seq_order_unit_w;
    IF (coalesce(dt_liberacao_w::text, '') = '') THEN
      nr_seq_order_unit_w := -1;
    END IF;
  END IF;
RETURN coalesce(nr_seq_order_unit_w, 0);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
