CREATE OR REPLACE FUNCTION get_nursing_atend ( nr_atendimento_p bigint, ie_profissional_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_return_w		varchar(4000);
nr_seq_nursing_team_w	numeric(30);

c01 CURSOR FOR	
SELECT b.nr_seq_nursing_team
from   atendimento_paciente a,
	   atend_profissional b
where  a.nr_atendimento = nr_atendimento_p
	   and a.nr_atendimento = b.nr_atendimento
	   and b.ie_profissional = coalesce(ie_profissional_p, 'P')
	   and b.dt_inicio_vigencia between a.dt_entrada and clock_timestamp()
order  by b.dt_inicio_vigencia;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	open c01;
	loop
	fetch c01 into	nr_seq_nursing_team_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_seq_nursing_team_w	:= nr_seq_nursing_team_w;
		end;
	end loop;
	close c01;

	if (nr_seq_nursing_team_w IS NOT NULL AND nr_seq_nursing_team_w::text <> '') then
		if (coalesce(ie_opcao_p,'N') = 'N') then
			ds_return_w := get_ds_nursing_team(nr_seq_nursing_team_w);
		else
			ds_return_w := nr_seq_nursing_team_w;
		end if;
	end if;
end if;

return	ds_return_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
