SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_ordered_on_behalf ( nr_sequencia_p bigint, ie_info_item_p text) RETURNS varchar AS $body$
DECLARE




  ds_retorno_w           varchar(255);



  nr_prescricao_w        bigint;



  nm_seocond_user_exisit varchar(1) :='N';

  -- ie_info_item_p  P - Procedure

  --                  D - Material
BEGIN



  IF (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') THEN



    BEGIN



      IF ( ie_info_item_p = 'P') THEN



        BEGIN



          SELECT OBTER_PRESCR_ITEM_CPOE(nr_sequencia,'P', nr_atendimento, cd_pessoa_fisica)



          INTO STRICT nr_prescricao_w



          FROM cpoe_procedimento



          WHERE nr_sequencia = nr_sequencia_p;



        END;



      ELSE



        BEGIN



          SELECT OBTER_PRESCR_ITEM_CPOE(nr_sequencia,CASE WHEN ie_controle_tempo='S' THEN 'SOL' WHEN ie_controle_tempo='N' THEN 'M' END , nr_atendimento, cd_pessoa_fisica)



          INTO STRICT nr_prescricao_w



          FROM cpoe_material



          WHERE nr_sequencia = nr_sequencia_p;



        END;



      END IF;



      SELECT CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END



      INTO STRICT nm_seocond_user_exisit



      FROM PRESCR_MEDICA_COMPL



      WHERE nr_prescricao       = nr_prescricao_w



      AND (NM_USUARIO_VALIDACAO IS NOT NULL AND NM_USUARIO_VALIDACAO::text <> '');



      SELECT CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END



      INTO STRICT ds_retorno_w



      FROM CPOE_ACTION_LOG_V



      WHERE nr_sequencia         = nr_sequencia_p



      AND (cd_medico IS NOT NULL AND cd_medico::text <> '')



      AND (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')



      AND coalesce(DT_CIENCIA_MEDICO::text, '') = ''



      AND nm_seocond_user_exisit ='S' 

      

      and ((ds_sigla not in ('M','P')) or (ds_sigla in ('M','P') and IE_ORDERED_ON_BEHALF ='S'));



    END;



  END IF;



RETURN ds_retorno_w;



END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION get_ordered_on_behalf ( nr_sequencia_p bigint, ie_info_item_p text) FROM PUBLIC;

