CREATE OR REPLACE FUNCTION get_order_type_group ( nr_seq_request_type_p bigint) RETURNS varchar AS $body$
DECLARE

			
nr_seq_cpoe_group_w		cpoe_tipo_pedido.nr_seq_cpoe_group%type;
nr_seq_sub_grp_w		cpoe_tipo_pedido.nr_seq_sub_grp%type;
			

BEGIN

if (nr_seq_request_type_p IS NOT NULL AND nr_seq_request_type_p::text <> '') then

	select	a.nr_seq_cpoe_group,
			a.nr_seq_sub_grp
	into STRICT	nr_seq_cpoe_group_w,
			nr_seq_sub_grp_w
	from	cpoe_tipo_pedido a
	where 	a.nr_sequencia = nr_seq_request_type_p;

	if (nr_seq_cpoe_group_w = 'D') then
		return 'DI';
	elsif (nr_seq_sub_grp_w = 'I') then
		return 'S';
	end if;

end if;	
	
return nr_seq_cpoe_group_w;

end;
$body$
