CREATE OR REPLACE FUNCTION get_order_unit_comment_cpoe ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE

    ds_return_w NUT_ATEND_SERV_DIA.DS_OBSERVACAO%type;

BEGIN
    BEGIN
    SELECT
        cpoe_get_list_of_comments(cd.nr_seq_cpoe_order_unit, 'CPOE_ORDER_UNIT', 'DTF', NULL) AS comments_list
    INTO STRICT ds_return_w
    FROM
        prescr_dieta             prd,
        nut_atend_serv_dia       nasd,
        nut_atend_serv_dia_rep   narp,
        cpoe_dieta               cd
    WHERE
        nasd.nr_sequencia = nr_sequencia_p
        AND nasd.nr_sequencia = narp.nr_seq_serv_dia
        AND narp.nr_prescr_oral = prd.nr_prescricao
        AND prd.nr_seq_dieta_cpoe = cd.nr_sequencia;

    EXCEPTION
      WHEN no_data_found THEN
        ds_return_w := null;
      WHEN too_many_rows THEN
        ds_return_w := null;
    END;

    RETURN ds_return_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
