SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_outpatient_consultation ( nr_atendimento_p atendimento_paciente.nr_atendimento%TYPE ) RETURNS varchar AS $body$
DECLARE

    nr_seq_agenda_w   atendimento_paciente_aux.nr_seq_agenda%TYPE;
    ie_response_w     varchar(300) := NULL;

BEGIN
    IF (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') THEN
        SELECT
            MAX(nr_seq_agenda)
        INTO STRICT nr_seq_agenda_w
        FROM
            atendimento_paciente_aux
        WHERE
            nr_atendimento = nr_atendimento_p;

        IF ( coalesce(nr_seq_agenda_w, 0) > 0 ) THEN
            SELECT
                obter_descricao_agenda(cd_agenda, 3, NULL, wheb_usuario_pck.get_cd_estabelecimento)
                || ' '
                || to_char(dt_agenda, 'dd/mm/yyyy hh24:mi')
            INTO STRICT ie_response_w
            FROM
                agenda_consulta
            WHERE
                nr_sequencia = nr_seq_agenda_w;

        END IF;

    END IF;

    RETURN ie_response_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
