CREATE OR REPLACE FUNCTION get_pac_medic_uso ( nr_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_medicamento_w varchar(100);
ds_medicamentos_w varchar(1000);

c01 CURSOR FOR
SELECT 	substr(obter_desc_material(cd_material),1,100)
from	paciente_medic_uso
where	nr_atendimento = nr_atendimento_p;


BEGIN

	open c01;
		loop
			fetch c01 into
				ds_medicamento_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
			
			if (ds_medicamentos_w IS NOT NULL AND ds_medicamentos_w::text <> '') then
				ds_medicamentos_w := ds_medicamentos_w || ', ';
			end if;
			ds_medicamentos_w := substr(ds_medicamentos_w || ds_medicamento_w, 1, 1000);
		end loop;
	close c01;
	
	return ds_medicamentos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
