SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_parameter_description ( cd_function_p bigint, nr_parameter_p bigint, ds_parameter_value_p text) RETURNS varchar AS $body$
DECLARE


ds_description_w	varchar(2000) := null;
ds_select_command_w	varchar(4000) := null;
cd_domain_w		funcao_parametro.cd_funcao%type;
ds_sql_w		funcao_parametro.ds_sql%type;


BEGIN

select	max(cd_dominio),
	max(ds_sql)
into STRICT	cd_domain_w,
	ds_sql_w
from	funcao_parametro
where	cd_funcao = cd_function_p
and	nr_sequencia = nr_parameter_p;

if (cd_domain_w IS NOT NULL AND cd_domain_w::text <> '') then
	
	select	max(obter_desc_expressao(cd_exp_valor_dominio, null))
	into STRICT	ds_description_w
	from	valor_dominio_v
	where	cd_dominio = cd_domain_w
	and	vl_dominio = ds_parameter_value_p;
	
elsif (ds_sql_w IS NOT NULL AND ds_sql_w::text <> '') then

	ds_select_command_w :=
	'select  ds from ( ' 
	|| 	ds_sql_w ||
	' ) where   cd = :cd';
	
	ds_description_w := obter_valor_dinamico_char_bv(ds_select_command_w, 'cd=' || ds_parameter_value_p || ';', ds_description_w);

end if;

return	obter_desc_param_senha(cd_function_p, nr_parameter_p, coalesce(ds_description_w, ds_parameter_value_p));

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
