CREATE OR REPLACE FUNCTION get_parent_comp_nav_schematic ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_obj_sup_w		bigint;
vl_retorno_w		bigint;
nr_sequencia_w		bigint;
ie_tipo_objeto_sup_w	varchar(10);
ie_tipo_componente_sup_w	varchar(10);
ie_tipo_obj_navegador_w	varchar(10);


BEGIN

select	nr_seq_obj_sup
into STRICT	nr_seq_obj_sup_w
from	objeto_schematic
where	nr_sequencia = nr_sequencia_p;

select	coalesce(ie_tipo_objeto,'XPTO'),
		coalesce(ie_tipo_obj_navegador,'XPTO'),
		coalesce(ie_tipo_componente, 'XPTO'),
		nr_sequencia
into STRICT	ie_tipo_objeto_sup_w,
		ie_tipo_obj_navegador_w,
		ie_tipo_componente_sup_w,
		nr_sequencia_w
from	objeto_schematic
where	nr_sequencia = nr_seq_obj_sup_w;

vl_retorno_w := null;

if	((ie_tipo_objeto_sup_w in ('TVN', 'T', 'IT', 'DDM', 'VMI', 'MN')) and (ie_tipo_obj_navegador_w <> 'TG')) or
	((ie_tipo_objeto_sup_w = 'C') and (ie_tipo_componente_sup_w in ('WDBP', 'WCP'))) then
	return nr_sequencia_w;
else
	return get_parent_comp_nav_schematic(nr_seq_obj_sup_w);
end if;

end;
$body$
