CREATE OR REPLACE FUNCTION get_parent_wdbp_wcp_schematic ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

nr_seq_obj_sup_w            objeto_schematic.nr_seq_obj_sup%type;
vl_retorno_w                integer := 0;
nr_sequencia_w              objeto_schematic.nr_sequencia%type;
ie_tipo_objeto_sup_w        objeto_schematic.ie_tipo_objeto%type;
ie_tipo_componente_sup_w    objeto_schematic.ie_tipo_componente%type;
ie_tipo_obj_navegador_w     objeto_schematic.ie_tipo_obj_navegador%type;
ds_valor_nulo_w             objeto_schematic.ie_tipo_objeto%type := 'XPTO';


BEGIN

select  max(nr_seq_obj_sup)
into STRICT    nr_seq_obj_sup_w
from    objeto_schematic
where   nr_sequencia = nr_sequencia_p;

select  coalesce(ie_tipo_objeto, ds_valor_nulo_w),
        coalesce(ie_tipo_obj_navegador, ds_valor_nulo_w),
        coalesce(ie_tipo_componente,  ds_valor_nulo_w),
        nr_sequencia
into STRICT    ie_tipo_objeto_sup_w,
        ie_tipo_obj_navegador_w,
        ie_tipo_componente_sup_w,
        nr_sequencia_w
from    objeto_schematic
where   nr_sequencia = nr_seq_obj_sup_w;

if  ((ie_tipo_objeto_sup_w = 'C') and (ie_tipo_componente_sup_w in ('WDBP', 'WCP'))) then
    vl_retorno_w := nr_sequencia_w;
else
    vl_retorno_w := get_parent_wdbp_wcp_schematic(nr_seq_obj_sup_w);
end if;

return vl_retorno_w;

EXCEPTION
    WHEN no_data_found then
        vl_retorno_w := 0;
        return vl_retorno_w;
    WHEN too_many_rows then
        vl_retorno_w := 0;
        return vl_retorno_w;

end;
$body$
