SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_patient_current_dept ( nr_atendimento_p bigint, cd_pessoa_fisica_p bigint, ie_opcao_p text, ie_informacao_p text) RETURNS varchar AS $body$
DECLARE

  ds_unidade_w				varchar(255);
  recent_nr_atendiemnto		bigint;

BEGIN
  IF ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') AND (coalesce(nr_atendimento_p,0) > 0))	THEN
	BEGIN
      ds_unidade_w := SUBSTR(Obter_Unidade_Atendimento(nr_atendimento_p,ie_opcao_p,ie_informacao_p),1,255);
    END;
  elsif ((cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') AND (coalesce(cd_pessoa_fisica_p,0) > 0))	THEN
	BEGIN
		SELECT MAX(nr_atendimento)
		INTO STRICT recent_nr_atendiemnto
		FROM atendimento_paciente_v
		WHERE cd_pessoa_fisica	= cd_pessoa_fisica_p
		AND coalesce(dt_alta::text, '') = '';
		IF (recent_nr_atendiemnto IS NOT NULL AND recent_nr_atendiemnto::text <> '')	THEN
			SELECT ds_setor_atendimento || ' ' || cd_unidade
			INTO STRICT ds_unidade_w
			FROM atendimento_paciente_v
			WHERE cd_pessoa_fisica = cd_pessoa_fisica_p
			AND nr_atendimento     = recent_nr_atendiemnto;
		ELSE
			select obter_desc_expressao(798768) into STRICT ds_unidade_w;
		END IF;
    END;
  END IF;
RETURN ds_unidade_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
