CREATE OR REPLACE FUNCTION get_pbs_prog_code_desc (cd_program_p text, ie_type_p text) RETURNS varchar AS $body$
DECLARE


/*
DS_SCHEDULE		'SC'
DS_PROGRAM		'PG'
*/
ds_result_w		PBS_PROG_CODE_DESC.DS_PROGRAM%type;


BEGIN
	if (cd_program_p IS NOT NULL AND cd_program_p::text <> '' AND ie_type_p IS NOT NULL AND ie_type_p::text <> '') then
		if ( ie_type_p = 'SC' ) then
			SELECT DS_SCHEDULE
			INTO STRICT ds_result_w
		  FROM PBS_PROG_CODE_DESC
			WHERE CD_PROGRAM_CODE = cd_program_p;
		elsif (ie_type_p = 'PG') then
			SELECT DS_PROGRAM
			INTO STRICT ds_result_w
		  FROM PBS_PROG_CODE_DESC
			WHERE CD_PROGRAM_CODE = cd_program_p;
		end if;
	end if;
return ds_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
