SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_permission_nursing_care (nr_sequencia_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
qt_registro_w           integer := 0;
qt_registro_norule_w           integer := 0;



BEGIN


if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

select count(1) into STRICT
qt_registro_norule_w
from    nursing_care_version a
where  a.nr_sequencia = nr_sequencia_p
and not exists ( SELECT 1 from nursing_care_department z
where z.nr_seq_nursing_care = a.nr_sequencia);

select count(1)
into STRICT qt_registro_w
from    nursing_care_version a,
nursing_care_department b
where   b.nr_seq_nursing_care = a.nr_sequencia
and a.nr_sequencia = nr_sequencia_p
and b.cd_setor_atendimento = Obter_Unidade_Atendimento(nr_atendimento_p,'A','CS')
and	coalesce(cd_departamento,coalesce(obter_departamento_data(nr_atendimento_p),0))	= coalesce(obter_departamento_data(nr_atendimento_p),0) 
and	coalesce(cd_unidade_basica,coalesce(Obter_Unidade_Atendimento(nr_atendimento_p,'A','UB'),'0'))	= coalesce(Obter_Unidade_Atendimento(nr_atendimento_p,'A','UB'),'0');

end if;
if ( qt_registro_w > 0 or qt_registro_norule_w > 0) then
	ds_retorno_w := 'S';	
end	if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
